import React from 'react';
import List from './list';
import {useState} from 'react'
import { Link, Route} from "wouter"
import Probando from "./prueba"
import Modal from "./Modal"

function ProductSection(props){

    const modalRef = React.useRef();
    const openModal = () => {
        modalRef.current.openModal()
    };
    const closeModal = () => {
        modalRef.current.closeModal()
    };


    const [dataFiltered, setDataFiltered] = useState([...props.data])

    const [inputValue, setInputValue] = useState({
        byname: "",
        bycatn: "",
        byrefn:"",
        bytype: ""
    })
    const handleInutChange = (event) => {
        console.log(event.target.value)
        setInputValue({
            ...inputValue,
            [event.target.name] : event.target.value
        })
    }
    const handleChange = (event) => {
        const value = event.target.value
        console.log(value)
        if (value === "All") {
            setDataFiltered(props.data)
        } else {
            setDataFiltered(props.data.filter(product => product.type === value))
        }
    }
    const searchByName = (event) => {
        event.preventDefault();
        const name = inputValue.byname;
        setDataFiltered(props.data.filter(product => product.name.toLowerCase().includes(name)));
        modalRef.current.closeModal()
    }
    const searchByCatN = (event) => {
        event.preventDefault();
        const catN = String(inputValue.bycatn);
        setDataFiltered(props.data.filter(product => String(product.catalog_number).includes(catN)));
        modalRef.current.closeModal()
    }
    const searchByRefN = (event) => {
        event.preventDefault();
        const refN = inputValue.byrefn;
        setDataFiltered(props.data.filter(product => product.reference_number.toLowerCase().includes(refN)));
        modalRef.current.closeModal()
    }
    const searchByType = () => {
        const type = inputValue.bytype;
        console.log(type)
        setDataFiltered(props.data.filter(product => product.type === type));
    }
 
   
    console.log(dataFiltered)
    return (
        <div>
            <div className="filter">
                <select id="select" name="bytype" onChange={handleInutChange, searchByType}>
                    {/* <option>Enimas</option>
                    <option>Droguería</option>
                    <option>Consumibles</option>
                    <option>Laboratorio</option>
                    <option>Vestuario</option>
                    <option>Papelería</option>
                    <option>Plástico</option>
                    <option>Químicos</option>
                    <option>Vidrio</option>
                    <option>Varios</option>
                    <option>Productos agregados</option> */}
                    <option selected="Type">Type</option>
                    <option value="All">All</option>
                    <option value="Chemicals">Quemicals</option>
                    <option value="Others">Others</option>
                </select>
                <button onClick={openModal}>Advanced Search</button>
            </div>
            <List data={dataFiltered} section="products"/>
            <a className="add-link">Add new product</a>
            <Modal ref={modalRef}>

                <div>
                    <h1>Advanced search</h1>
                    <button onClick={closeModal}className="close">Close</button>
                </div>
                <form className="form">
                    <div>
                        <label htmlFor="byname">Search by product name</label>
                        <input type="text" name="byname" placeholder="Product name" onChange={handleInutChange}/>
                    </div>
                    <button onClick={searchByName}>Search</button>
                    <div>
                        <label htmlFor="bycatn">Search by catalog number </label>
                        <input type="text" name="bycatn" placeholder="Catalog number" onChange={handleInutChange}/>
                    </div>
                    <button onClick={searchByCatN}>Search</button>
                    <div>
                        <label htmlFor="byrefn">Search by reference number </label>
                        <input type="text" name="byrefn" placeholder="Reference number" onChange={handleInutChange}/>
                    </div>
                    <button onClick={searchByRefN}>Search</button>
                </form>
            </Modal>
        </div>
    )
}

export default ProductSection
