import React from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';
import {useState} from 'react'

function SignUp() {

    const [signupInputValue, setSignupInputValue] = useState({
        fullname: "",
        email: "",
        password: ""
    })
    const handleSignupInputChange = (event) => {
        setSignInputValue({
            ...signupInputValue,
            [event.target.name] : event.target.value
        })
    }
    const register = () => {
        axios.post(`${apiURL}product/newproduct`, 
        // {
        //     catalog_number: Number(addInputValue.catalog_number),
        //     name: addInputValue.name,
        //     type : addInputValue.type,
        //     trading_house : addInputValue.trading_house,
        //     reference_number: addInputValue.reference_number,
        //     price : Number(addInputValue.price),
        //     information : addInputValue.information
        // }
        {...addInputValue}
        )
        .then(res => {
            console.log("producto aÃ±adido!")
            console.log(res.data)
            setResponse({...response,
                success: true,
                msg: `${res.data.name} has been added to the product list!`
            })
            openResponseModal()
            getData()
        })
        .catch(error => {
            console.log(error)
            console.log("hay un error")
            setResponse({...response,
                error: true,
                msg: error.response.data
            })
            openResponseModal()
        });
    }
    


    return (
        <div>
            <img></img>
            <form>
                <div>
                    <label htmlFor="fullname">Fullname</label>
                    <input type="text" name="fullname" placeholder="Fullname" />
                </div>
                <div>
                    <label htmlFor="Laboral position">Password</label>
                    <input type="text" name="password" placeholder="position" />
                </div>
                <div>
                    <label htmlFor="email">Email</label>
                    <input type="text" name="email" placeholder="E-mail" />
                </div>
                <div>
                    <label htmlFor="password">Password</label>
                    <input type="text" name="password" placeholder="Password" />
                </div>
                <div>
                    <label htmlFor="rol">Rol</label>
                    <select id="select" name="rol">
                        <option value="administrator">Administrator</option>
                        <option value="validator">Validator</option>
                        <option value="user">User</option>
                    </select>
                </div>
            </form>
            <div>
                <button>CREATE ACCOUNT</button>
                <p>Already have an account? <Link to="/login">Login here</Link></p>
            </div>
        </div>
    )
}

export default SignUp
