import React from 'react';
import axios from 'axios';
import {useState, useEffect} from 'react'
import {Redirect, Link, Route, Switch} from 'react-router-dom'
import Modal from "./modal"
import ModalResponse from './modalResponse';
import SuccessResponse from "./successResponse"
import ErrorResponse from "./errorResponse"
import ProductListItem from './productListItem'
import apiURL from '../services/apiURL'
import RequestsListItem from './requestsListItem'

function RequestsSection() {

    //CONSIGUIENDO LAS DATAS DE PEDIDOS SEGÚN ESTADO DESDE DB
    const [dataNewRequests, setDataNewRequests] = useState([])

    //waiting
    async function getData() {
        const dataBase = await axios.get(`${apiURL}order/waiting`);
        setDataNewRequests(dataBase.data)
    }
    useEffect(() => {
        getData()
    },[])

    //validated
    const [dataValidatedRequests, setdataValidatedRequests] = useState([])

    async function getData() {
        const dataBase = await axios.get(`${apiURL}order/validated`);
        setdataValidatedRequests(dataBase.data)
    }
    useEffect(() => {
        getData()
    },[])

    //received
    const [dataReceivedRequests, setdataReceivedRequests] = useState([])

    async function getData() {
        const dataBase = await axios.get(`${apiURL}order/received`);
        setdataReceivedRequests(dataBase.data)
    }
    useEffect(() => {
        getData()
    },[])

    //all
    const [dataAllRequests, setdataAllRequests] = useState([])

    async function getData() {
        const dataBase = await axios.get(`${apiURL}order/`);
        setdataAllRequests(dataBase.data)
    }
    useEffect(() => {
        getData()
    },[])

    return (
        <div>
            <div>
                <button>New</button>
                <button>Validated</button>
                <button>Received</button>
                <button>All</button>
            </div>
            <Switch >
            <Route path="requests/newrequests">
            <div className="list">
                {dataNewRequests.map((item, index) => {
                    return <RequestsListItem product={item} key={index} />
                })}
            </div>
            </Route>
            <Route path="requests/validatedrequests">
            <div className="list">
                {dataValidatedRequests.map((item, index) => {
                    return <RequestsListItem product={item} key={index} />
                })}
            </div>
            </Route>
            <Route path="requests/receivedrequests">
            <div className="list">
                {dataReceivedRequests.map((item, index) => {
                    return <RequestsListItem product={item} key={index} />
                })}
            </div>
            </Route>
            <Route path="requests/allrequests">
            <div className="list">
                {dataAllRequests.map((item, index) => {
                    return <RequestsListItem product={item} key={index} />
                })}
            </div>
            </Route>
            </Switch>
            <div>
                <img></img>
                <Link to="/products">Add new request</Link>
            </div>
        </div>
    )
}

export default RequestsSection
