import React from 'react';
import List from './list';
import {useState} from 'react'
import { Link, Route} from "wouter"
import Probando from "./prueba"
import Modal from "./Modal"

function ProductSection(props){


    const [dataFiltered, setDataFiltered] = useState([...props.data])

    const handleChange = (event) => {
        const value = event.target.value
        console.log(value)
        if (value === "All") {
            setDataFiltered(props.data)
        } else {
            setDataFiltered(props.data.filter(product => product.type === value))
        }
    }
    
    const openModal = () => {
        console.log("Open modal")
    }
   

    return (
        <div>
            <div className="filter">
                <select id="select" name="" onChange={handleChange}>
                    {/* <option>Enimas</option>
                    <option>Droguería</option>
                    <option>Consumibles</option>
                    <option>Laboratorio</option>
                    <option>Vestuario</option>
                    <option>Papelería</option>
                    <option>Plástico</option>
                    <option>Químicos</option>
                    <option>Vidrio</option>
                    <option>Varios</option>
                    <option>Productos agregados</option> */}
                    <option value="All">All</option>
                    <option value="Chemicals">Quemicals</option>
                    <option value="Others">Others</option>
                </select>
                <button onClick={openModal}>Advanced Search</button>
            </div>
            <List data={dataFiltered} section="products"/>
            <a className="add-link">Add new product</a>
            <Modal />
        </div>
    )
}

export default ProductSection
